create database dbSoftTransportuse dbSoftTransport--AA12DD0000create table tblEmployee(Id int identity(101,1) primary key,Name varchar(20) not null,Password varchar(20) not null,Location varchar(20),Phone varchar(20),Vechicle_Id char(8))create table tblDriver(ID int identity(1000,1) primary key,Name varchar(20),Phone varchar(20),Status varchar(50) check(Status in ('active','not active')))drop table tblDrivercreate table tblVehicle(Vechicle_number char(8) primary key,Type varchar(10),Capacity int,Driver_ID int references tblDriver(id),Filled_Status int ,Status varchar(50) check(Status in ('running','discard')))alter table tblEmployeeadd constraint fk_VID foreign key(Vechicle_Id) references tblVehicle(Vechicle_number)create proc proc_InsertEmployee(@eName varchar(20),@ePassword varchar(20),@eLocation varchar(20),@ePhone varchar(20),@eVehicleId char(8))as Insert into tblEmployee(Name, Password,Location,Phone,Vechicle_Id) values(@eName,@ePassword,@eLocation,@ePhone,@eVehicleId)drop proc proc_InsertEmployeecreate proc proc_UpdatePassword(@eid int,@ePassword varchar(20))asupdate tblEmployee set Password =@ePassword where id=@eiddrop proc proc_UpdatePasswordcreate proc proc_GetAllEmployeesasselect* from tblEmployeeexec proc_GetAllEmployees;drop proc proc_GetAllEmployeescreate proc proc_UpdateVehicleId(@eid int,@eVehicleId char(8))asupdate tblEmployee set Vechicle_Id =@eVehicleId where id=@eiddrop proc proc_UpdateVehicleIddrop table tblEmployee;drop table tblVehicle;create proc proc_InsertDriver(@dName varchar(20),@dPhone varchar(20),@dStatus varchar(50))as Insert into tblDriver(Name,Phone,Status) values(@dName,@dPhone,@dStatus)drop proc proc_InsertDriverselect * from tblDriverdrop table tblDrivercreate proc proc_UpdateDriverPhone(@did int,@dPhone varchar(20))asupdate tblDriver set Phone =@dPhone where id=@diddrop proc proc_UpdateDriverPhonecreate Proc proc_UpdateDriverStatus(@did int, @dStatus varchar(50))asupdate tblDriver set Status=@dStatus where id=@diddrop proc proc_UpdateDriverStatuscreate proc proc_GetAllDriversasselect* from tblDriverdrop proc proc_GetAllDriverscreate proc proc_InsertVehicle(@vVehicleNumber char(8),@vType varchar(10),@vCapacity int,@vDriverId int,@vFilledStatus int,@vStatus varchar(50))as Insert into tblVehicle(Vechicle_number,Type,Capacity,Driver_ID,Filled_Status,Status)values(@vVehicleNumber,@vType,@vCapacity,@vDriverId,@vFilledStatus,@vStatus)drop proc proc_InsertVehiclecreate Proc proc_UpdateVehicleCapacity(@vVehicleNumber char(8), @vCapacity int)asupdate tblVehicle set Capacity=@vCapacity where Vechicle_number=@vVehicleNumberdrop proc proc_UpdateVehicleCapacitycreate Proc proc_UpdateDriverId(@vVehicleNumber char(8), @vDriverId int)asupdate tblVehicle set Driver_ID=@vDriverId where Vechicle_number=@vVehicleNumberdrop proc proc_UpdateDriverIdcreate Proc proc_UpdateVehicleFilledStatus(@vVehicleNumber char(8), @vFilledStatus int)asupdate tblVehicle set Filled_Status=@vFilledStatus where Vechicle_number=@vVehicleNumberdrop proc proc_UpdateDriverIdcreate Proc proc_UpdateVehicleStatus(@vVehicleNumber char(8), @vStatus varchar(50))asupdate tblVehicle set Status=@vStatus where Vechicle_number=@vVehicleNumberdrop proc proc_UpdateVehicleStatuscreate proc proc_GetAllVehiclesasselect* from tblVehicledrop proc proc_GetAllVehicles